script:
// Mobile menu functionality
document.addEventListener('DOMContentLoaded', function() {
  const mobileMenuButton = document.getElementById('mobile-menu-button');
  const mobileMenuClose = document.getElementById('mobile-menu-close');
  const mobileMenu = document.getElementById('mobile-menu');

  mobileMenuButton.addEventListener('click', function() {
    mobileMenu.classList.add('active');
  });

  mobileMenuClose.addEventListener('click', function() {
    mobileMenu.classList.remove('active');
  });

  // FAQ toggle functionality
  const faqButtons = document.querySelectorAll('#faq button');

  faqButtons.forEach(button => {
    button.addEventListener('click', function() {
      const content = this.nextElementSibling;
      const icon = this.querySelector('svg');

      // Check if content is expanded
      const isExpanded = content.style.maxHeight;

      // Reset all FAQs
      faqButtons.forEach(otherButton => {
        if (otherButton !== button) {
          const otherContent = otherButton.nextElementSibling;
          const otherIcon = otherButton.querySelector('svg');
          otherContent.style.maxHeight = null;
          otherIcon.style.transform = 'rotate(0deg)';
        }
      });

      // Toggle current FAQ
      if (isExpanded) {
        content.style.maxHeight = null;
        icon.style.transform = 'rotate(0deg)';
      } else {
        content.style.maxHeight = content.scrollHeight + 'px';
        icon.style.transform = 'rotate(180deg)';
      }
    });
  });

  // Smooth scrolling for anchor links
  document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function(e) {
      e.preventDefault();

      // Close mobile menu if open
      if (mobileMenu.classList.contains('active')) {
        mobileMenu.classList.remove('active');
      }

      // Get the target element
      const targetId = this.getAttribute('href');
      if (targetId === '#') return; // Skip if just "#"

      const targetElement = document.querySelector(targetId);
      if (!targetElement) return; // Skip if element doesn't exist

      // Scroll to target
      window.scrollTo({
        top: targetElement.offsetTop - 80, // Adjust for header height
        behavior: 'smooth'
      });

      // Update URL without page reload
      history.pushState(null, null, targetId);
    });
  });

  // Active link highlighting based on scroll position
  const sections = document.querySelectorAll('section[id]');
  const navLinks = document.querySelectorAll('header nav a');

  function highlightNavLink() {
    const scrollPos = window.scrollY + 100; // Adjust for header height

    sections.forEach(section => {
      const sectionTop = section.offsetTop;
      const sectionHeight = section.offsetHeight;
      const sectionId = section.getAttribute('id');

      if (scrollPos >= sectionTop && scrollPos < sectionTop + sectionHeight) {
        // Remove active class from all links
        navLinks.forEach(link => {
          link.classList.remove('text-gray-900');
          link.classList.add('text-gray-600');
        });

        // Add active class to current link
        const currentLink = document.querySelector(`header nav a[href="#${sectionId}"]`);
        if (currentLink) {
          currentLink.classList.remove('text-gray-600');
          currentLink.classList.add('text-gray-900');
        }
      }
    });
  }

  window.addEventListener('scroll', highlightNavLink);

  // Initialize by triggering once on load
  highlightNavLink();
});
